      .where(
        and(
          eq(salesTransactions.stationId, stationId),
          gte(salesTransactions.transactionDate, startDate),
          lte(salesTransactions.transactionDate, endDate)
        )
      )
      .groupBy(salesTransactions.transactionDate)
