/* Print styles for invoices */
@media print {
  body {
    background: #fff !important;
    color: #000 !important;
    color-scheme: light !important;
  }

  html.dark body {
    background: #fff !important;
    color: #000 !important;
  }

  /* Hide everything by default when printing */
  body * {
    visibility: hidden;
  }

  /* Only show the invoice print area */
  #invoice-print,
  #invoice-print * {
    visibility: visible;
  }

  /* Position the invoice print area to fill the page */
  #invoice-print {
    position: absolute;
    left: 0;
    top: 0;
    margin: 0;
    padding: 20px;
    width: 100%;
    background: #fff !important;
  }

  /* Ensure all text is black in print */
  #invoice-print * {
    color: #000 !important;
    background: transparent !important;
  }

  /* Special handling for borders and separators */
  #invoice-print .border {
    border-color: #000 !important;
  }

