import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { format } from "date-fns";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Progress } from "@/components/ui/progress";
import { Download, Clock, AlertTriangle, CheckCircle, AlertCircle } from "lucide-react";
import { useAuth } from "@/hooks/useAuth";
import { useCurrency } from "@/contexts/CurrencyContext";

interface AgingBucketData {
  id: string;
  invoiceNumber?: string;
  orderNumber?: string;
  customerName?: string;
  supplierName?: string;
  transactionDate?: string;
  orderDate?: string;
  dueDate?: string;
  totalAmount?: number;
  outstandingAmount: number;
  paidAmount?: number;
  currencyCode: string;
  daysOverdue: number;
}

interface AgingReportData {
  type: 'receivable' | 'payable';
  buckets: {
    current: AgingBucketData[];
    days30: AgingBucketData[];
    days60: AgingBucketData[];
    days90: AgingBucketData[];
    over90: AgingBucketData[];
  };
  totals: {
    current: number;
    days30: number;
    days60: number;
    days90: number;
    over90: number;
  };
  grandTotal: number;
}

export default function AgingReports() {
  const { user } = useAuth();
  const { formatCurrency } = useCurrency();
  const [reportType, setReportType] = useState<'receivable' | 'payable'>('receivable');

  const { data: agingReport, isLoading } = useQuery<AgingReportData>({
    queryKey: [`/api/reports/aging/${user?.stationId}?type=${reportType}`],
    enabled: !!user?.stationId,
  });

  const exportToCSV = () => {
    if (!agingReport) return;

    const isReceivable = reportType === 'receivable';
    const headers = isReceivable 
      ? ['Invoice Number', 'Customer Name', 'Transaction Date', 'Due Date', 'Outstanding Amount', 'Currency', 'Days Overdue', 'Status']
      : ['Order Number', 'Supplier Name', 'Order Date', 'Due Date', 'Outstanding Amount', 'Currency', 'Days Overdue', 'Status'];

    const rows: string[][] = [];

    // Add data from all buckets
    Object.entries(agingReport.buckets).forEach(([bucketName, items]) => {
      items.forEach(item => {
        const daysOverdue = item.daysOverdue || 0;
        const status = daysOverdue <= 0 ? 'Current' : 
                     daysOverdue <= 30 ? '1-30 Days' :
                     daysOverdue <= 60 ? '31-60 Days' :
                     daysOverdue <= 90 ? '61-90 Days' : '90+ Days';

        if (isReceivable) {
          rows.push([
            item.invoiceNumber || 'N/A',
            item.customerName || 'Unknown Customer',
            item.transactionDate ? format(new Date(item.transactionDate), 'yyyy-MM-dd') : 'N/A',
            item.dueDate ? format(new Date(item.dueDate), 'yyyy-MM-dd') : 'N/A',
            (item.outstandingAmount || 0).toString(),
            item.currencyCode || '',
            (item.daysOverdue || 0).toString(),
            status
          ]);
        } else {
          rows.push([
            item.orderNumber || 'N/A',
            item.supplierName || 'Unknown Supplier',
            item.orderDate ? format(new Date(item.orderDate), 'yyyy-MM-dd') : 'N/A',
            item.dueDate ? format(new Date(item.dueDate), 'yyyy-MM-dd') : 'N/A',
            (item.outstandingAmount || 0).toString(),
            item.currencyCode || '',
            (item.daysOverdue || 0).toString(),
            status
          ]);
        }
      });
    });

    const csvContent = [headers, ...rows]
      .map(row => row.map(cell => `"${cell}"`).join(','))
      .join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `aging-report-${reportType}-${format(new Date(), 'yyyy-MM-dd')}.csv`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const getBadgeVariant = (daysOverdue: number) => {
    const days = daysOverdue || 0;
    if (days <= 0) return "default";
    if (days <= 30) return "secondary";
    if (days <= 60) return "outline";
    if (days <= 90) return "destructive";
    return "destructive";
  };

  const getBadgeIcon = (daysOverdue: number) => {
    const days = daysOverdue || 0;
    if (days <= 0) return <CheckCircle className="w-3 h-3" />;
    if (days <= 30) return <Clock className="w-3 h-3" />;
    if (days <= 90) return <AlertTriangle className="w-3 h-3" />;
    return <AlertCircle className="w-3 h-3" />;
  };

  const formatAmount = (amount: number, currencyCode?: string) => {
    return formatCurrency(amount);
  };

  const renderBucketCard = (bucketName: string, bucketData: AgingBucketData[], total: number, label: string) => {
    const grandTotal = agingReport?.grandTotal || 0;
    const percentage = grandTotal > 0 ? Math.round((total / grandTotal) * 100) : 0;

    return (
      <Card key={bucketName} className="bg-white dark:bg-gray-800" data-testid={`card-bucket-${bucketName}`}>
        <CardHeader className="pb-3">
          <CardTitle className="text-sm font-medium flex items-center justify-between">
            <span data-testid={`text-bucket-${bucketName}`}>{label}</span>
            <Badge variant={bucketName === 'current' ? 'default' : bucketName === 'over90' ? 'destructive' : 'secondary'} data-testid={`badge-count-${bucketName}`}>
              {bucketData.length}
            </Badge>
          </CardTitle>
          <CardDescription className="text-2xl font-bold" data-testid={`text-amount-${bucketName}`}>
            {formatAmount(total)}
          </CardDescription>
        </CardHeader>
        <CardContent>
          <Progress value={percentage} className="mb-2" data-testid={`progress-${bucketName}`} />
          <p className="text-xs text-muted-foreground" data-testid={`text-percentage-${bucketName}`}>{percentage}% of total outstanding</p>
        </CardContent>
      </Card>
    );
  };

  if (isLoading) {
    return (
      <div className="p-6 space-y-6">
        <div className="flex items-center justify-between">
          <div>
            <div className="h-8 bg-gray-200 dark:bg-gray-700 rounded w-48 animate-pulse"></div>
            <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded w-64 mt-2 animate-pulse"></div>
          </div>
          <div className="h-10 bg-gray-200 dark:bg-gray-700 rounded w-32 animate-pulse"></div>
        </div>
        <div className="grid grid-cols-1 md:grid-cols-5 gap-4">
          {Array.from({ length: 5 }).map((_, i) => (
            <div key={i} className="h-32 bg-gray-200 dark:bg-gray-700 rounded animate-pulse"></div>
          ))}
        </div>
        <div className="h-64 bg-gray-200 dark:bg-gray-700 rounded animate-pulse"></div>
      </div>
    );
  }

  if (!agingReport) {
    return (
      <div className="p-6">
        <div className="text-center py-12">
          <AlertTriangle className="mx-auto h-12 w-12 text-gray-400" />
          <h3 className="mt-4 text-lg font-semibold text-gray-900 dark:text-gray-100">No Aging Data Available</h3>
          <p className="mt-2 text-gray-600 dark:text-gray-400">Unable to load aging report data. Please try again later.</p>
        </div>
      </div>
    );
  }

  const buckets = [
    { name: 'current', data: agingReport.buckets.current, total: agingReport.totals.current, label: 'Current (0 days)' },
    { name: 'days30', data: agingReport.buckets.days30, total: agingReport.totals.days30, label: '1-30 Days' },
    { name: 'days60', data: agingReport.buckets.days60, total: agingReport.totals.days60, label: '31-60 Days' },
    { name: 'days90', data: agingReport.buckets.days90, total: agingReport.totals.days90, label: '61-90 Days' },
    { name: 'over90', data: agingReport.buckets.over90, total: agingReport.totals.over90, label: '90+ Days' }
  ];

  const isReceivable = reportType === 'receivable';

  return (
    <div className="p-6 space-y-6 bg-gray-50 dark:bg-gray-900 min-h-screen">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-gray-900 dark:text-gray-100" data-testid="text-title">
            Aging Reports
          </h1>
          <p className="text-gray-600 dark:text-gray-400 mt-1" data-testid="text-subtitle">
            Track outstanding {reportType === 'receivable' ? 'receivables' : 'payables'} by aging periods
          </p>
        </div>
        <Button onClick={exportToCSV} className="bg-blue-600 hover:bg-blue-700" data-testid="button-export">
          <Download className="w-4 h-4 mr-2" />
          Export CSV
        </Button>
      </div>

      <Tabs value={reportType} onValueChange={(value) => setReportType(value as 'receivable' | 'payable')} data-testid="tabs-report-type">
        <TabsList className="grid w-full grid-cols-2">
          <TabsTrigger value="receivable" data-testid="tab-receivable">
            Accounts Receivable
          </TabsTrigger>
          <TabsTrigger value="payable" data-testid="tab-payable">
            Accounts Payable
          </TabsTrigger>
        </TabsList>

        <TabsContent value={reportType} className="space-y-6">
          {/* Summary Cards */}
          <div className="grid grid-cols-1 md:grid-cols-5 gap-4">
            {buckets.map(bucket => renderBucketCard(bucket.name, bucket.data, bucket.total, bucket.label))}
          </div>

          {/* Grand Total */}
          <Card className="bg-white dark:bg-gray-800">
            <CardHeader>
              <CardTitle className="text-lg" data-testid="text-grand-total-title">
                Total Outstanding {isReceivable ? 'Receivables' : 'Payables'}
              </CardTitle>
              <CardDescription className="text-3xl font-bold text-blue-600" data-testid="text-grand-total">
                {formatAmount(agingReport?.grandTotal || 0)}
              </CardDescription>
            </CardHeader>
          </Card>

          {/* Detailed Table */}
          <Card className="bg-white dark:bg-gray-800">
            <CardHeader>
              <CardTitle data-testid="text-details-title">Transaction Details</CardTitle>
              <CardDescription>
                Detailed breakdown of all outstanding {isReceivable ? 'receivables' : 'payables'}
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="overflow-x-auto">
                <Table data-testid="table-details">
                  <TableHeader>
                    <TableRow>
                      <TableHead>{isReceivable ? 'Invoice #' : 'Order #'}</TableHead>
                      <TableHead>{isReceivable ? 'Customer' : 'Supplier'}</TableHead>
                      <TableHead>{isReceivable ? 'Transaction Date' : 'Order Date'}</TableHead>
                      <TableHead>Due Date</TableHead>
                      <TableHead>Outstanding</TableHead>
                      <TableHead>Days Overdue</TableHead>
                      <TableHead>Status</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {buckets.map(bucket => 
                      bucket.data.map(item => (
                        <TableRow key={item.id} data-testid={`row-transaction-${item.id}`}>
                          <TableCell className="font-medium" data-testid={`text-number-${item.id}`}>
                            {isReceivable ? (item.invoiceNumber || 'N/A') : (item.orderNumber || 'N/A')}
                          </TableCell>
                          <TableCell data-testid={`text-entity-${item.id}`}>
                            {isReceivable ? (item.customerName || 'Unknown Customer') : (item.supplierName || 'Unknown Supplier')}
                          </TableCell>
                          <TableCell data-testid={`text-date-${item.id}`}>
                            {(() => {
                              const dateValue = isReceivable ? item.transactionDate : item.orderDate;
                              return dateValue ? format(new Date(dateValue), 'MMM dd, yyyy') : 'N/A';
                            })()}
                          </TableCell>
                          <TableCell data-testid={`text-due-${item.id}`}>
                            {item.dueDate ? format(new Date(item.dueDate), 'MMM dd, yyyy') : 'N/A'}
                          </TableCell>
                          <TableCell className="font-semibold" data-testid={`text-amount-${item.id}`}>
                            {formatAmount(item.outstandingAmount || 0, item.currencyCode)}
                          </TableCell>
                          <TableCell data-testid={`text-days-${item.id}`}>
                            {(item.daysOverdue || 0) > 0 ? `${item.daysOverdue || 0} days` : '-'}
                          </TableCell>
                          <TableCell data-testid={`badge-status-${item.id}`}>
                            {(() => {
                              const daysOverdue = item.daysOverdue || 0;
                              return (
                                <Badge variant={getBadgeVariant(daysOverdue)} className="flex items-center gap-1 w-fit">
                                  {getBadgeIcon(daysOverdue)}
                                  {daysOverdue <= 0 ? 'Current' : 
                                   daysOverdue <= 30 ? '1-30 Days' :
                                   daysOverdue <= 60 ? '31-60 Days' :
                                   daysOverdue <= 90 ? '61-90 Days' : '90+ Days'}
                                </Badge>
                              );
                            })()}
                          </TableCell>
                        </TableRow>
                      ))
                    )}
                    {buckets.every(bucket => bucket.data.length === 0) && (
                      <TableRow>
                        <TableCell colSpan={7} className="text-center py-8 text-gray-500" data-testid="text-no-data">
                          No outstanding {isReceivable ? 'receivables' : 'payables'} found
                        </TableCell>
                      </TableRow>
                    )}
                  </TableBody>
                </Table>
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

client/src/pages/CashReconciliation.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import type { SalesTransaction, Expense } from "@shared/schema";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { useCurrency } from "@/contexts/CurrencyContext";
import { formatCompactNumber } from "@/lib/utils";
import { RefreshCw, CreditCard, TrendingUp, TrendingDown, Printer } from "lucide-react";

export default function CashReconciliation() {
  const { user } = useAuth();
  const { toast } = useToast();
  const { formatCurrency, currencyConfig } = useCurrency();
  const [shift, setShift] = useState("day");
  const [reconciliationDate, setReconciliationDate] = useState(new Date().toISOString().split('T')[0]);
  const [denominations, setDenominations] = useState([
    { value: 2000, count: 0, total: 0 },
    { value: 500, count: 0, total: 0 },
    { value: 200, count: 0, total: 0 },
    { value: 100, count: 0, total: 0 },
    { value: 50, count: 0, total: 0 },
    { value: 20, count: 0, total: 0 },
    { value: 10, count: 0, total: 0 },
    { value: 5, count: 0, total: 0 },
    { value: 2, count: 0, total: 0 },
    { value: 1, count: 0, total: 0 }
  ]);

  // Calculate shift time range
  const getShiftDateRange = () => {
    const baseDate = new Date(reconciliationDate);
    let startTime, endTime;

    if (shift === 'day') {
      startTime = new Date(baseDate);
      startTime.setHours(6, 0, 0, 0);
      endTime = new Date(baseDate);
      endTime.setHours(18, 0, 0, 0);
    } else if (shift === 'night') {
      startTime = new Date(baseDate);
      startTime.setHours(18, 0, 0, 0);
      endTime = new Date(baseDate);
      endTime.setDate(endTime.getDate() + 1);
      endTime.setHours(6, 0, 0, 0);
    } else {
      startTime = new Date(baseDate);
      startTime.setHours(0, 0, 0, 0);
      endTime = new Date(baseDate);
      endTime.setHours(23, 59, 59, 999);
    }

    return { startTime: startTime.toISOString(), endTime: endTime.toISOString() };
  };

  const { startTime, endTime } = getShiftDateRange();

  // Fetch sales data for the selected shift
  const { data: salesTransactions = [], isLoading: salesLoading } = useQuery<SalesTransaction[]>({
    queryKey: ["/api/sales", user?.stationId, startTime, endTime],
    enabled: !!user?.stationId,
  });

  // Fetch expenses data for the selected shift  
  const { data: expenses = [], isLoading: expensesLoading } = useQuery<Expense[]>({
    queryKey: ["/api/expenses", user?.stationId, startTime, endTime],
    enabled: !!user?.stationId,
  });

  const isLoading = salesLoading || expensesLoading;

  // Calculate real cash data based on transactions
  const cashSales = salesTransactions
    .filter((t: SalesTransaction) => t.paymentMethod === 'cash')
    .reduce((sum: number, t: SalesTransaction) => sum + parseFloat(t.totalAmount || '0'), 0);

  const cardSales = salesTransactions
    .filter((t: SalesTransaction) => ['card', 'credit_card', 'debit_card'].includes(t.paymentMethod || ''))
    .reduce((sum: number, t: SalesTransaction) => sum + parseFloat(t.totalAmount || '0'), 0);

  const creditSales = salesTransactions
    .filter((t: SalesTransaction) => t.paymentMethod === 'credit')
    .reduce((sum: number, t: SalesTransaction) => sum + parseFloat(t.totalAmount || '0'), 0);

  const totalExpenses = expenses
    .reduce((sum: number, e: Expense) => sum + parseFloat(e.amount || '0'), 0);

  const openingBalance = 5000; // This could be from previous day's closing or a settings table
  const expectedCash = openingBalance + cashSales - totalExpenses;
  const actualCash = denominations.reduce((sum, d) => sum + d.total, 0);
  const difference = actualCash - expectedCash;

  const cashData = {
    openingBalance,
    expectedCash,
    actualCash,
    difference,
    cashSales,
    cardSales,
    creditSales,
    expenses: totalExpenses
  };

  // Handle denomination count changes
  const updateDenomination = (index: number, count: number) => {
    const newDenominations = [...denominations];
    newDenominations[index].count = Math.max(0, count);
    newDenominations[index].total = newDenominations[index].value * newDenominations[index].count;
    setDenominations(newDenominations);
  };

  if (isLoading) {
    return (
      <div className="space-y-6">
        <div className="animate-pulse">
          <div className="h-8 bg-muted rounded w-1/3 mb-4"></div>
          <div className="h-4 bg-muted rounded w-1/2 mb-8"></div>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
            {[...Array(4)].map((_, i) => (
              <div key={i} className="h-24 bg-muted rounded"></div>
            ))}
          </div>
          <div className="h-96 bg-muted rounded"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6 fade-in">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h3 className="text-2xl font-semibold text-card-foreground">Cash Reconciliation</h3>
          <p className="text-muted-foreground">Daily cash balancing and shift-wise reporting</p>
        </div>
        <div className="flex items-center space-x-2">
          <Button onClick={() => {
            toast({
              title: "Reconciliation Started",
              description: "Cash reconciliation process has been initiated",
            });
          }} data-testid="button-start-reconciliation">
            <RefreshCw className="mr-2 h-4 w-4" /> Start Reconciliation
          </Button>
          <Button variant="outline" onClick={() => {
            const printWindow = window.open('', '_blank');
            if (!printWindow) return;

            const htmlContent = `
              <!DOCTYPE html>
              <html>
                <head>
                  <title>Cash Reconciliation Report</title>
                  <style>
                    @page { margin: 0.5in; size: A4; }
                    body { font-family: Arial, sans-serif; line-height: 1.4; color: #000; margin: 0; padding: 20px; }
                    .header { text-align: center; margin-bottom: 30px; border-bottom: 2px solid #333; padding-bottom: 20px; }
                    .summary { display: grid; grid-template-columns: repeat(2, 1fr); gap: 20px; margin-bottom: 30px; }
                    .summary-item { padding: 15px; background: #f3f4f6; border-radius: 8px; }
                    .amount { font-size: 18px; font-weight: bold; }
                  </style>
                </head>
                <body>
                  <div class="header">
                    <h1>Cash Reconciliation Report</h1>
                    <p>${shift.charAt(0).toUpperCase() + shift.slice(1)} Shift - ${new Date(reconciliationDate).toLocaleDateString()}</p>
                  </div>
                  <div class="summary">
                    <div class="summary-item">
                      <div>Opening Balance</div>
                      <div class="amount">${formatCurrency(cashData.openingBalance)}</div>
                    </div>
                    <div class="summary-item">
                      <div>Expected Cash</div>
                      <div class="amount">${formatCurrency(cashData.expectedCash)}</div>
                    </div>
                    <div class="summary-item">
                      <div>Actual Cash</div>
                      <div class="amount">${formatCurrency(cashData.actualCash)}</div>
                    </div>
                    <div class="summary-item">
                      <div>Difference</div>
                      <div class="amount">${formatCurrency(Math.abs(cashData.difference))}</div>
                    </div>
                  </div>
                </body>
              </html>
            `;

            printWindow.document.write(htmlContent);
            printWindow.document.close();
            printWindow.onload = () => {
              setTimeout(() => {
                printWindow.print();
                printWindow.close();
              }, 500);
            };
          }} data-testid="button-print-report">
            <Printer className="mr-2 h-4 w-4" /> Print Report
          </Button>
        </div>
      </div>

      {/* Reconciliation Controls */}
      <Card>
        <CardContent className="p-6">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <label className="block text-sm font-medium text-card-foreground mb-2">Shift</label>
              <Select value={shift} onValueChange={setShift}>
                <SelectTrigger data-testid="select-shift">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="day">Day Shift (6 AM - 6 PM)</SelectItem>
                  <SelectItem value="night">Night Shift (6 PM - 6 AM)</SelectItem>
                  <SelectItem value="full">Full Day (24 Hours)</SelectItem>
                </SelectContent>
              </Select>
            </div>
            <div>
              <label className="block text-sm font-medium text-card-foreground mb-2">Date</label>
              <Input
                type="date"
                value={reconciliationDate}
                onChange={(e) => setReconciliationDate(e.target.value)}
                data-testid="input-reconciliation-date"
              />
            </div>
            <div className="flex items-end">
              <Button className="w-full" data-testid="button-load-data">
                Load Shift Data
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Cash Summary */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        <Card className="bg-gradient-to-br from-green-500 to-green-600 text-white border-0">
          <CardContent className="p-4">
            <div className="text-center">
              <div className="text-2xl font-bold" data-testid="opening-balance">{formatCurrency(cashData.openingBalance)}</div>
              <div className="text-sm text-green-100">Opening Balance</div>
            </div>
          </CardContent>
        </Card>
        <Card className="bg-gradient-to-br from-blue-500 to-blue-600 text-white border-0">
          <CardContent className="p-4">
            <div className="text-center">
              <div className="text-2xl font-bold" data-testid="expected-cash">{formatCurrency(cashData.expectedCash)}</div>
              <div className="text-sm text-blue-100">Expected Cash</div>
            </div>
          </CardContent>
        </Card>
        <Card className="bg-gradient-to-br from-purple-500 to-purple-600 text-white border-0">
          <CardContent className="p-4">
            <div className="text-center">
              <div className="text-2xl font-bold" data-testid="actual-cash">{formatCurrency(cashData.actualCash)}</div>
              <div className="text-sm text-purple-100">Actual Cash</div>
            </div>
          </CardContent>
        </Card>
        <Card className="bg-gradient-to-br from-red-500 to-red-600 text-white border-0">
          <CardContent className="p-4">
            <div className="text-center">
              <div className="text-2xl font-bold" data-testid="cash-difference">{formatCurrency(Math.abs(cashData.difference))}</div>
              <div className="text-sm text-red-100">
                {cashData.difference >= 0 ? 'Excess' : 'Shortage'}
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Cash Counting */}
        <Card>
          <CardHeader>
            <CardTitle>Cash Denomination Count</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {denominations.map((denom, index) => (
                <div key={denom.value} className="grid grid-cols-4 gap-4 items-center">
                  <div className="font-medium">{currencyConfig.symbol}{denom.value}</div>
                  <div className="text-center">Ã—</div>
                  <Input
                    type="number"
                    value={denom.count}
                    onChange={(e) => updateDenomination(index, parseInt(e.target.value) || 0)}
                    className="text-center"
                    data-testid={`denom-count-${denom.value}`}
                  />
                  <div className="text-right font-semibold" data-testid={`denom-total-${denom.value}`}>
                    {formatCurrency(denom.total)}
                  </div>
                </div>
              ))}
              <div className="border-t pt-3 mt-4">
                <div className="grid grid-cols-4 gap-4 font-bold">
                  <div>Total Cash:</div>
                  <div></div>
                  <div></div>
                  <div className="text-right text-lg" data-testid="total-counted-cash">
                    {formatCurrency(cashData.actualCash)}
                  </div>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Sales Summary */}
        <Card>
          <CardHeader>
            <CardTitle>Shift Sales Summary</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div className="flex justify-between items-center p-3 bg-green-50 rounded-md">
                <span className="font-medium">Cash Sales</span>
                <span className="text-lg font-bold text-green-600" data-testid="cash-sales">
                  {formatCurrency(cashData.cashSales)}
                </span>
              </div>
              <div className="flex justify-between items-center p-3 bg-blue-50 rounded-md">
                <span className="font-medium">Card Sales</span>
                <span className="text-lg font-bold text-blue-600" data-testid="card-sales">
                  {formatCurrency(cashData.cardSales)}
                </span>
              </div>
              <div className="flex justify-between items-center p-3 bg-orange-50 rounded-md">
                <span className="font-medium">Credit Sales</span>
                <span className="text-lg font-bold text-orange-600" data-testid="credit-sales">
                  {formatCurrency(cashData.creditSales)}
                </span>
              </div>
              <div className="flex justify-between items-center p-3 bg-red-50 rounded-md">
                <span className="font-medium">Expenses Paid</span>
                <span className="text-lg font-bold text-red-600" data-testid="expenses-paid">
                  {formatCurrency(cashData.expenses)}
                </span>
              </div>

              <div className="border-t pt-4 mt-6">
                <div className="flex justify-between items-center">
                  <span className="text-lg font-semibold">Net Cash Movement:</span>
                  <span className="text-xl font-bold text-primary" data-testid="net-cash-movement">
                    {formatCurrency(cashData.cashSales - cashData.expenses)}
                  </span>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Reconciliation Status */}
      <Card>
        <CardHeader>
          <CardTitle>Reconciliation Status</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex items-center justify-between p-4 bg-muted rounded-lg">
            <div>
              <div className="font-semibold text-lg">
                Cash Reconciliation - {shift.charAt(0).toUpperCase() + shift.slice(1)} Shift
              </div>
              <div className="text-sm text-muted-foreground">
                Date: {new Date(reconciliationDate).toLocaleDateString('en-IN')}
              </div>
            </div>
            <div className="text-right">
              <Badge
                variant={Math.abs(cashData.difference) <= 100 ? 'default' : 'destructive'}
                className={Math.abs(cashData.difference) <= 100 ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'}
                data-testid="reconciliation-status"
              >
                {Math.abs(cashData.difference) <= 100 ? 'Balanced' : 'Variance Detected'}
              </Badge>
              {cashData.difference !== 0 && (
                <div className={`mt-2 font-semibold ${cashData.difference > 0 ? 'text-green-600' : 'text-red-600'}`}>
                  {cashData.difference > 0 ? 'Excess: ' : 'Shortage: '}
                  {formatCurrency(Math.abs(cashData.difference))}
                </div>
              )}
            </div>
          </div>

          <div className="mt-4 flex justify-end space-x-2">
            <Button variant="outline" onClick={() => {
              localStorage.setItem('cashReconciliationDraft', JSON.stringify({
                shift,
                reconciliationDate,
                denominations,
                cashData
              }));
              toast({
                title: "Draft Saved",
                description: "Cash reconciliation draft has been saved locally",
              });
            }} data-testid="button-save-draft">
              Save Draft
            </Button>
            <Button onClick={() => {
              toast({
                title: "Reconciliation Completed",
                description: `Cash reconciliation for ${shift} shift completed successfully`,
              });
            }} data-testid="button-complete-reconciliation">
              Complete Reconciliation
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

