import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { useAuth } from "@/hooks/useAuth";
import { Line, LineChart, ResponsiveContainer, XAxis, YAxis } from "recharts";
import type { Tank, SalesTransaction, Customer, Product } from "@shared/schema";
import { useCurrency } from "@/contexts/CurrencyContext";
import { useLocation } from "wouter";
import { Download, FileText, ShoppingCart, Users, BarChart3, AlertCircle, DollarSign, TrendingUp, Fuel, Clock, AlertTriangle, Check } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { formatCompactNumber } from "@/lib/utils";

export default function Dashboard() {
  const { user } = useAuth();
  const { formatCurrency, formatCurrencyCompact } = useCurrency();
  const [, setLocation] = useLocation();
  const { toast } = useToast();

  const { data: dashboardStats, isLoading: statsLoading } = useQuery({
    queryKey: ["/api/dashboard", user?.stationId],
    enabled: !!user?.stationId,
  });

  const { data: tanks = [], isLoading: tanksLoading } = useQuery<Tank[]>({
    queryKey: ["/api/tanks", user?.stationId],
    enabled: !!user?.stationId,
  });

  const { data: recentSales = [], isLoading: salesLoading } = useQuery<SalesTransaction[]>({
    queryKey: ["/api/sales", user?.stationId, "recent"],
    enabled: !!user?.stationId,
  });

  const { data: products = [] } = useQuery<Product[]>({
    queryKey: ["/api/products"],
  });

  const { data: customers = [] } = useQuery<Customer[]>({
    queryKey: ["/api/customers"],
  });

  // Calculate chart data from dashboard stats (last 7 days)
  const generateChartData = () => {
    if (!dashboardStats || typeof dashboardStats !== 'object' || !('weeklySales' in dashboardStats)) return [];

    const days = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
    const today = new Date().getDay();
    const chartData = [];

    for (let i = 6; i >= 0; i--) {
      const dayIndex = (today - i + 7) % 7;
      const dayData = (dashboardStats as any).weeklySales?.find((d: any) => d.dayOfWeek === dayIndex);
      chartData.push({
        day: days[dayIndex],
        sales: dayData ? parseFloat(dayData.totalAmount || '0') : 0
      });
    }
    return chartData;
  };

  // Calculate stock value from tanks
  const calculateStockValue = () => {
    if (!tanks.length || !products.length) return 0;

    return tanks.reduce((total, tank) => {
      const product = products.find(p => p.id === tank.productId);
      if (product) {
        const stockValue = parseFloat(tank.currentStock || '0') * parseFloat(product.currentPrice || '0');
        return total + stockValue;
      }
      return total;
    }, 0);
  };

  // Get overdue customers count
  const getOverdueCustomersCount = () => {
    return customers.filter((customer: Customer) => 
      parseFloat(customer.outstandingAmount || '0') > 0
    ).length;
  };

  // Quick action handlers
  const handleNewSale = () => {
    setLocation('/pos');
  };

  const handleViewReports = () => {
    setLocation('/financial-reports');
  };

  const handleStockStatus = () => {
    setLocation('/stock');
  };

  const handleCustomerPayments = () => {
    setLocation('/accounts-receivable');
  };

  const handleTankMonitoring = () => {
    setLocation('/tanks');
  };

  const handleDailyReports = () => {
    setLocation('/daily-reports');
  };


  const isLoading = statsLoading || tanksLoading || salesLoading;

  if (isLoading) {
    return (
      <div className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {[...Array(4)].map((_, i) => (
            <Card key={i} className="animate-pulse">
              <CardContent className="p-6">
                <div className="h-20 bg-muted rounded"></div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-8 fade-in">
      {/* Key Performance Indicators */}
      <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-4 gap-6">
        <Card className="bg-gradient-to-br from-green-500 to-green-600 text-white border-0">
          <CardContent className="p-4 sm:p-6">
            <div className="flex items-center justify-between">
              <div className="flex-1 min-w-0">
                <p className="text-green-100 text-xs sm:text-sm font-medium">Today's Sales</p>
                <p className="text-lg sm:text-2xl lg:text-3xl font-bold truncate" data-testid="todays-sales">
                  {formatCompactNumber(parseFloat((dashboardStats as any)?.todaysSales?.totalAmount || '0'), { currency: 'PKR' })}
                </p>
                <p className="text-green-100 text-xs sm:text-sm">{(dashboardStats as any)?.todaysSales?.count || 0} transactions</p>
              </div>
              <div className="opacity-80 flex-shrink-0"><DollarSign className="w-6 h-6 sm:w-8 sm:h-8 lg:w-10 lg:h-10" /></div>
            </div>
            <div className="mt-2 sm:mt-4 flex items-center">
              <span className="text-green-100 text-xs">+12% vs yesterday</span>
            </div>
          </CardContent>
        </Card>

        <Card className="bg-gradient-to-br from-blue-500 to-blue-600 text-white border-0">
          <CardContent className="p-4 sm:p-6">
            <div className="flex items-center justify-between">
              <div className="flex-1 min-w-0">
                <p className="text-blue-100 text-xs sm:text-sm font-medium">Monthly Revenue</p>
                <p className="text-lg sm:text-2xl lg:text-3xl font-bold truncate" data-testid="monthly-revenue">
                  {formatCompactNumber((dashboardStats as any)?.monthlySales?.totalAmount ? parseFloat((dashboardStats as any).monthlySales.totalAmount) : 0, { currency: 'PKR' })}
                </p>
                <p className="text-blue-100 text-xs sm:text-sm">{(dashboardStats as any)?.monthlySales?.count || 0} transactions total</p>
              </div>
              <div className="opacity-80 flex-shrink-0"><TrendingUp className="w-6 h-6 sm:w-8 sm:h-8 lg:w-10 lg:h-10" /></div>
            </div>
            <div className="mt-2 sm:mt-4 flex items-center">
              <span className="text-blue-100 text-xs">On track for target</span>
            </div>
          </CardContent>
        </Card>

        <Card className="bg-gradient-to-br from-purple-500 to-purple-600 text-white border-0">
          <CardContent className="p-4 sm:p-6">
            <div className="flex items-center justify-between">
              <div className="flex-1 min-w-0">
                <p className="text-purple-100 text-xs sm:text-sm font-medium">Stock Value</p>
                <p className="text-lg sm:text-2xl lg:text-3xl font-bold truncate" data-testid="stock-value">
                  {formatCompactNumber(calculateStockValue(), { currency: 'PKR' })}
                </p>
                <p className="text-purple-100 text-xs sm:text-sm">All tanks combined</p>
              </div>
              <div className="opacity-80 flex-shrink-0"><Fuel className="w-6 h-6 sm:w-8 sm:h-8 lg:w-10 lg:h-10" /></div>
            </div>
            <div className="mt-2 sm:mt-4 flex items-center">
              <span className="text-purple-100 text-xs">3 days avg inventory</span>
            </div>
          </CardContent>
        </Card>

        <Card className="bg-gradient-to-br from-orange-500 to-orange-600 text-white border-0">
          <CardContent className="p-4 sm:p-6">
            <div className="flex items-center justify-between">
              <div className="flex-1 min-w-0">
                <p className="text-orange-100 text-xs sm:text-sm font-medium">Outstanding</p>
                <p className="text-lg sm:text-2xl lg:text-3xl font-bold truncate" data-testid="outstanding-amount">
                  {formatCompactNumber((dashboardStats as any)?.outstanding?.totalOutstanding ? parseFloat((dashboardStats as any).outstanding.totalOutstanding) : 0, { currency: 'PKR' })}
                </p>
                <p className="text-orange-100 text-xs sm:text-sm">Credit customers</p>
              </div>
              <div className="opacity-80 flex-shrink-0"><Clock className="w-6 h-6 sm:w-8 sm:h-8 lg:w-10 lg:h-10" /></div>
            </div>
            <div className="mt-2 sm:mt-4 flex items-center">
              <span className="text-orange-100 text-xs">{getOverdueCustomersCount()} customers pending</span>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Quick Actions */}
      <Card>
        <CardHeader>
          <CardTitle>Quick Actions</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-6 gap-4">
            <Button 
              variant="outline" 
              className="flex flex-col items-center gap-2 h-20"
              onClick={handleNewSale}
              data-testid="button-new-sale"
            >
              <ShoppingCart className="w-6 h-6 text-green-600" />
              <span className="text-sm font-medium">New Sale</span>
            </Button>

            <Button 
              variant="outline" 
              className="flex flex-col items-center gap-2 h-20"
              onClick={handleViewReports}
              data-testid="button-view-reports"
            >
              <BarChart3 className="w-6 h-6 text-blue-600" />
              <span className="text-sm font-medium">View Reports</span>
            </Button>

            <Button 
              variant="outline" 
              className="flex flex-col items-center gap-2 h-20"
              onClick={handleStockStatus}
              data-testid="button-stock-status"
            >
              <FileText className="w-6 h-6 text-purple-600" />
              <span className="text-sm font-medium">Stock Status</span>
            </Button>

            <Button 
              variant="outline" 
              className="flex flex-col items-center gap-2 h-20"
              onClick={handleCustomerPayments}
              data-testid="button-customer-payments"
            >
              <Users className="w-6 h-6 text-orange-600" />
              <span className="text-sm font-medium">Customer Payments</span>
            </Button>

            <Button 
              variant="outline" 
              className="flex flex-col items-center gap-2 h-20"
              onClick={handleTankMonitoring}
              data-testid="button-tank-monitoring"
            >
              <AlertCircle className="w-6 h-6 text-red-600" />
              <span className="text-sm font-medium">Tank Monitoring</span>
            </Button>

            <Button 
              variant="outline" 
              className="flex flex-col items-center gap-2 h-20"
              onClick={handleDailyReports}
              data-testid="button-daily-reports"
            >
              <Download className="w-6 h-6 text-indigo-600" />
              <span className="text-sm font-medium">Daily Reports</span>
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Charts and Analytics */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <Card>
          <CardHeader>
            <CardTitle>Sales Trend (Last 7 Days)</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="h-64">
              <ResponsiveContainer width="100%" height="100%">
                <LineChart data={generateChartData()}>
                  <XAxis dataKey="day" />
                  <YAxis />
                  <Line type="monotone" dataKey="sales" stroke="hsl(var(--primary))" strokeWidth={2} />
                </LineChart>
              </ResponsiveContainer>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Today's Product Sales</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {(dashboardStats as any)?.productSales?.map((product: any, index: number) => {
                const colors = ['bg-green-500', 'bg-blue-500', 'bg-yellow-500', 'bg-red-500'];
                return (
                  <div key={product.productId} className="flex items-center justify-between p-3 bg-muted rounded-md">
                    <div className="flex items-center">
                      <div className={`w-4 h-4 ${colors[index % colors.length]} rounded mr-3`}></div>
                      <span className="font-medium">{product.productName}</span>
                    </div>
                    <div className="text-right">
                      <div className="font-semibold" data-testid={`${product.productName.toLowerCase()}-sales`}>
                        {formatCurrency(parseFloat(product.totalAmount || '0'))}
                      </div>
                      <div className="text-sm text-muted-foreground">
                        {parseFloat(product.totalQuantity || '0').toLocaleString()} L
                      </div>
                    </div>
                  </div>
                );
              }) || (
                <div className="text-center text-muted-foreground py-4">
                  No product sales data available
                </div>
              )}
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Recent Transactions and Alerts */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <Card>
          <CardHeader>
            <div className="flex items-center justify-between">
              <CardTitle>Recent Transactions</CardTitle>
              <button 
                className="text-primary hover:text-primary/80 text-sm font-medium transition-colors" 
                onClick={() => setLocation('/sales-history')}
                data-testid="button-view-all"
              >
                View All
              </button>
            </div>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {recentSales.length > 0 ? recentSales.slice(0, 3).map((transaction: SalesTransaction, index: number) => {
                const timeAgo = transaction.transactionDate ? new Date(transaction.transactionDate).toLocaleString() : 'N/A';
                return (
                  <div key={transaction.id} className="flex items-center justify-between p-3 border border-border rounded-md">
                    <div>
                      <div className="font-medium text-card-foreground" data-testid={`transaction-id-${index}`}>
                        {transaction.invoiceNumber}
                      </div>
                      <div className="text-sm text-muted-foreground">
                        {transaction.paymentMethod === 'cash' ? 'Cash Sale' : transaction.paymentMethod === 'credit' ? 'Credit Sale' : 'Card Sale'}
                      </div>
                      <div className="text-xs text-muted-foreground">{timeAgo}</div>
                    </div>
                    <div className="text-right">
                      <div className={`font-semibold ${
                        transaction.paymentMethod === 'cash' ? 'text-green-600' : 
                        transaction.paymentMethod === 'credit' ? 'text-blue-600' : 'text-purple-600'
                      }`}>
                        {formatCurrency(parseFloat(transaction.totalAmount || '0'))}
                      </div>
                    </div>
                  </div>
                );
              }) : (
                <div className="text-center text-muted-foreground py-4">
                  No recent transactions
                </div>
              )}
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>System Alerts</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {/* Low Stock Alerts */}
              {tanks.filter(tank => {
                const currentStock = parseFloat(tank.currentStock || '0');
                const minimumLevel = parseFloat(tank.minimumLevel || '500');
                return currentStock <= minimumLevel;
              }).slice(0, 2).map(tank => {
                const product = products.find(p => p.id === tank.productId);
                return (
                  <div key={tank.id} className="p-3 bg-yellow-50 border border-yellow-200 rounded-md">
                    <div className="flex items-start">
                      <AlertTriangle className="w-5 h-5 text-yellow-500 mr-2" />
                      <div>
                        <div className="text-sm font-medium text-yellow-800">Low Stock Alert</div>
                        <div className="text-xs text-yellow-600">
                          {tank.name} ({product?.name}) - Only {parseFloat(tank.currentStock || '0').toLocaleString()}L remaining
                        </div>
                      </div>
                    </div>
                  </div>
                );
              })}

              {/* Overdue Customers */}
              {customers.filter(customer => parseFloat(customer.outstandingAmount || '0') > 50000).slice(0, 1).map(customer => (
                <div key={customer.id} className="p-3 bg-red-50 border border-red-200 rounded-md">
                  <div className="flex items-start">
                    <DollarSign className="w-5 h-5 text-red-500 mr-2" />
                    <div>
                      <div className="text-sm font-medium text-red-800">Payment Overdue</div>
                      <div className="text-xs text-red-600">
                        {customer.name} - â‚¹{parseFloat(customer.outstandingAmount || '0').toLocaleString()} outstanding
                      </div>
                    </div>
                  </div>
                </div>
              ))}

              {/* Show message if no alerts */}
              {tanks.filter(tank => parseFloat(tank.currentStock || '0') <= parseFloat(tank.minimumLevel || '500')).length === 0 && 
               customers.filter(customer => parseFloat(customer.outstandingAmount || '0') > 50000).length === 0 && (
                <div className="p-3 bg-green-50 border border-green-200 rounded-md">
                  <div className="flex items-start">
                    <Check className="w-5 h-5 text-green-500 mr-2" />
                    <div>
                      <div className="text-sm font-medium text-green-800">All Systems Normal</div>
                      <div className="text-xs text-green-600">No critical alerts at this time</div>
                    </div>
                  </div>
                </div>
              )}
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/ExpenseManagement.tsx.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Textarea } from "@/components/ui/textarea";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { useCurrency } from "@/contexts/CurrencyContext";
import { apiRequest } from "@/lib/api";
import { CreditCard, Receipt, Eye, Edit, Trash2, Plus, Download, Printer } from "lucide-react";
import { DeleteConfirmation } from "@/components/ui/delete-confirmation";

const expenseSchema = z.object({
  description: z.string().min(1, "Description is required"),
  amount: z.string().min(1, "Amount is required"),
  category: z.string().min(1, "Category is required"),
  paymentMethod: z.string().min(1, "Payment method is required"),
  expenseDate: z.string().min(1, "Date is required"),
  receiptNumber: z.string().optional(),
  notes: z.string().optional(),
});

interface Expense {
  id: string;
  description: string;
  amount: string;
  category: string;
  paymentMethod: string;
  expenseDate: string;
  receiptNumber?: string;
  notes?: string;
  stationId: string;
  userId: string;
  createdAt: string;
}

export default function ExpenseManagement() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const { formatCurrency } = useCurrency();
  const [open, setOpen] = useState(false);
  const [editExpenseId, setEditExpenseId] = useState<string | null>(null);
  const [deleteConfirmOpen, setDeleteConfirmOpen] = useState(false);
  const [expenseToDelete, setExpenseToDelete] = useState<Expense | null>(null);

  const form = useForm({
    resolver: zodResolver(expenseSchema),
    defaultValues: {
      description: "",
      amount: "",
      category: "",
      paymentMethod: "",
      expenseDate: new Date().toISOString().split('T')[0],
      receiptNumber: "",
      notes: "",
    },
    mode: "onChange"
  });

  const { data: expenses = [], isLoading } = useQuery<Expense[]>({
    queryKey: ["/api/expenses", user?.stationId],
    queryFn: () => apiRequest("GET", `/api/expenses?stationId=${user?.stationId}`).then(res => res.json()),
    enabled: !!user?.stationId,
  });

  const createExpenseMutation = useMutation({
    mutationFn: async (data: any) => {
      console.log("Creating expense with data:", data);

      if (!user?.stationId || !user?.id) {
        throw new Error("User session not properly loaded");
      }

      const expenseData = {
        ...data,
        stationId: user.stationId,
        userId: user.id,
        amount: parseFloat(data.amount).toString(),
      };

      console.log("Final expense data being sent:", expenseData);

      const response = await apiRequest("POST", "/api/expenses", expenseData);
      if (!response.ok) {
        const error = await response.json().catch(() => ({ message: response.statusText }));
        throw new Error(error.message || 'Failed to create expense');
      }
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Expense recorded",
        description: "Expense has been recorded successfully",
      });
      setOpen(false);
      form.reset();
      queryClient.invalidateQueries({ queryKey: ["/api/expenses", user?.stationId] });
    },
    onError: (error: any) => {
      console.error("Expense creation error:", error);
      toast({
        title: "Error",
        description: error.message || "Failed to record expense",
        variant: "destructive",
      });
    },
  });

  const updateExpenseMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: any }) => {
      const expenseData = {
        ...data,
        amount: parseFloat(data.amount).toString(),
      };
      const response = await apiRequest("PUT", `/api/expenses/${id}`, expenseData);
      if (!response.ok) throw new Error('Failed to update expense');
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Expense updated",
        description: "Expense has been updated successfully",
      });
      setEditExpenseId(null);
      setOpen(false);
      queryClient.invalidateQueries({ queryKey: ["/api/expenses", user?.stationId] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update expense",
        variant: "destructive",
      });
    },
  });

  const deleteExpenseMutation = useMutation({
    mutationFn: async (id: string) => {
      const response = await apiRequest("DELETE", `/api/expenses/${id}`);
      if (!response.ok) throw new Error('Failed to delete expense');
    },
    onSuccess: () => {
      toast({
        title: "Expense deleted",
        description: "Expense has been deleted successfully",
      });
      setDeleteConfirmOpen(false);
      setExpenseToDelete(null);
      queryClient.invalidateQueries({ queryKey: ["/api/expenses", user?.stationId] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to delete expense",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: any) => {
    console.log("Expense form submission:", { data, editExpenseId, user });

    if (editExpenseId) {
      console.log("Updating expense with ID:", editExpenseId);
      updateExpenseMutation.mutate({ id: editExpenseId, data });
    } else {
      console.log("Creating new expense");
      createExpenseMutation.mutate(data);
    }
  };

  const handleEditExpense = (expense: Expense) => {
    setEditExpenseId(expense.id);
    form.reset({
      description: expense.description,
      amount: expense.amount,
      category: expense.category,
      paymentMethod: expense.paymentMethod,
      expenseDate: expense.expenseDate ? new Date(expense.expenseDate).toISOString().split('T')[0] : new Date().toISOString().split('T')[0],
      receiptNumber: expense.receiptNumber || "",
      notes: expense.notes || "",
    });
    setOpen(true);
  };

  const handleDeleteExpense = (expense: Expense) => {
    setExpenseToDelete(expense);
    setDeleteConfirmOpen(true);
  };

  const confirmDeleteExpense = () => {
    if (expenseToDelete) {
      deleteExpenseMutation.mutate(expenseToDelete.id);
    }
  };

  const handleViewReceipt = (expense: Expense) => {
    // Generate receipt content for viewing
    const receiptWindow = window.open('', '_blank');
    if (!receiptWindow) return;

    const htmlContent = `
      <!DOCTYPE html>
      <html>
        <head>
          <title>Expense Receipt - ${expense.receiptNumber || expense.id}</title>
          <style>
            @page { margin: 0.5in; size: A4; }
            body { font-family: Arial, sans-serif; line-height: 1.4; color: #000; margin: 0; padding: 20px; }
            .header { text-align: center; margin-bottom: 30px; border-bottom: 2px solid #333; padding-bottom: 20px; }
            .header h1 { color: #2563eb; margin: 0; }
            .expense-details { background: #f9fafb; padding: 15px; border-radius: 8px; margin-bottom: 20px; }
            .footer { text-align: center; margin-top: 30px; padding-top: 15px; border-top: 1px solid #e5e7eb; font-size: 12px; color: #666; }
          </style>
        </head>
        <body>
          <div class="header">
            <h1>FuelFlow Station</h1>
            <h2>Expense Receipt</h2>
            <p>Receipt #: ${expense.receiptNumber || expense.id}</p>
          </div>

          <div class="expense-details">
            <h3>Expense Details</h3>
            <p><strong>Description:</strong> ${expense.description}</p>
            <p><strong>Amount:</strong> ${formatCurrency(parseFloat(expense.amount))}</p>
            <p><strong>Category:</strong> ${expense.category}</p>
            <p><strong>Payment Method:</strong> ${expense.paymentMethod}</p>
            <p><strong>Date:</strong> ${new Date(expense.expenseDate).toLocaleDateString()}</p>
            ${expense.notes ? `<p><strong>Notes:</strong> ${expense.notes}</p>` : ''}
          </div>

          <div class="footer">
            <p>This is a computer-generated receipt from FuelFlow Management System</p>
          </div>
        </body>
      </html>
    `;

    receiptWindow.document.write(htmlContent);
    receiptWindow.document.close();

    receiptWindow.onload = () => {
      setTimeout(() => {
        receiptWindow.print();
        receiptWindow.close();
      }, 500);
    };
  };

  if (isLoading) {
    return (
      <div className="space-y-6">
        <div className="animate-pulse">
          <div className="h-8 bg-muted rounded w-1/3 mb-4"></div>
          <div className="h-4 bg-muted rounded w-1/2 mb-8"></div>
          <div className="h-96 bg-muted rounded"></div>
        </div>
      </div>
    );
  }

  const totalExpenses = expenses.reduce((sum, expense) => sum + parseFloat(expense.amount), 0);

  return (
    <div className="space-y-6 fade-in">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h3 className="text-2xl font-semibold text-card-foreground">Expense Management</h3>
          <p className="text-muted-foreground">Track and manage business expenses</p>
        </div>
        <Dialog open={open} onOpenChange={(isOpen) => { 
          setOpen(isOpen);
          if (!isOpen) { 
            setEditExpenseId(null); 
            form.reset();
          } 
        }}>
          <DialogTrigger asChild>
            <Button data-testid="button-add-expense">
              <Plus className="w-4 h-4 mr-2" />
              Record Expense
            </Button>
          </DialogTrigger>
          <DialogContent className="sm:max-w-[500px]">
            <DialogHeader>
              <DialogTitle>{editExpenseId ? "Edit Expense" : "Record New Expense"}</DialogTitle>
            </DialogHeader>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                <FormField
                  control={form.control}
                  name="description"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Description *</FormLabel>
                      <FormControl>
                        <Input placeholder="Office supplies, fuel, etc." {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <div className="grid grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
                    name="amount"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Amount *</FormLabel>
                        <FormControl>
                          <Input type="number" step="0.01" placeholder="0.00" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="category"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Category *</FormLabel>
                        <Select onValueChange={field.onChange} value={field.value}>
                          <FormControl>
                            <SelectTrigger>
                              <SelectValue placeholder="Select category" />
                            </SelectTrigger>
                          </FormControl>
                          <SelectContent>
                            <SelectItem value="fuel">Fuel</SelectItem>
                            <SelectItem value="maintenance">Maintenance</SelectItem>
                            <SelectItem value="utilities">Utilities</SelectItem>
                            <SelectItem value="supplies">Supplies</SelectItem>
                            <SelectItem value="salaries">Salaries</SelectItem>
                            <SelectItem value="other">Other</SelectItem>
                          </SelectContent>
                        </Select>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>
                <div className="grid grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
                    name="paymentMethod"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Payment Method *</FormLabel>
                        <Select onValueChange={field.onChange} value={field.value}>
                          <FormControl>
                            <SelectTrigger>
                              <SelectValue placeholder="Select method" />
                            </SelectTrigger>
                          </FormControl>
                          <SelectContent>
                            <SelectItem value="cash">Cash</SelectItem>
                            <SelectItem value="bank_transfer">Bank Transfer</SelectItem>
                            <SelectItem value="cheque">Cheque</SelectItem>
                            <SelectItem value="credit_card">Credit Card</SelectItem>
                          </SelectContent>
                        </Select>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="expenseDate"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Date *</FormLabel>
                        <FormControl>
                          <Input 
                            type="date" 
                            {...field} 
                            max="9999-12-31"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>
                <FormField
                  control={form.control}
                  name="receiptNumber"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Receipt Number</FormLabel>
                      <FormControl>
                        <Input placeholder="Optional receipt reference" {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={form.control}
                  name="notes"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Notes</FormLabel>
                      <FormControl>
                        <Textarea placeholder="Additional details" {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <div className="flex justify-end space-x-2 pt-4">
                  <Button type="button" variant="outline" onClick={() => setOpen(false)}>
                    Cancel
                  </Button>
                  <Button type="submit" disabled={createExpenseMutation.isPending || updateExpenseMutation.isPending}>
                    {editExpenseId ? "Update Expense" : "Record Expense"}
                  </Button>
                </div>
              </form>
            </Form>
          </DialogContent>
        </Dialog>
      </div>

      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <Card>
          <CardContent className="p-4">
            <div className="text-2xl font-bold text-primary">{expenses.length}</div>
            <div className="text-sm text-muted-foreground">Total Expenses</div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-4">
            <div className="text-2xl font-bold text-red-600">{formatCurrency(totalExpenses)}</div>
            <div className="text-sm text-muted-foreground">Total Amount</div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-4">
            <div className="text-2xl font-bold text-blue-600">
              {expenses.filter(e => new Date(e.expenseDate).getMonth() === new Date().getMonth()).length}
            </div>
            <div className="text-sm text-muted-foreground">This Month</div>
          </CardContent>
        </Card>
      </div>

      {/* Expenses List */}
      <Card>
        <CardHeader>
          <CardTitle>Expense History</CardTitle>
        </CardHeader>
        <CardContent className="p-0">
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead className="bg-muted">
                <tr>
                  <th className="text-left p-3 font-medium">Date</th>
                  <th className="text-left p-3 font-medium">Description</th>
                  <th className="text-left p-3 font-medium">Category</th>
                  <th className="text-right p-3 font-medium">Amount</th>
                  <th className="text-left p-3 font-medium">Payment Method</th>
                  <th className="text-center p-3 font-medium">Actions</th>
                </tr>
              </thead>
              <tbody>
                {expenses.length > 0 ? expenses.map((expense, index) => (
                  <tr key={expense.id} className="border-b border-border hover:bg-muted/50">
                    <td className="p-3 text-sm">
                      {new Date(expense.expenseDate).toLocaleDateString()}
                    </td>
                    <td className="p-3 font-medium">{expense.description}</td>
                    <td className="p-3">
                      <Badge variant="outline">{expense.category}</Badge>
                    </td>
                    <td className="p-3 text-right font-semibold text-red-600">
                      {formatCurrency(parseFloat(expense.amount))}
                    </td>
                    <td className="p-3 capitalize">{expense.paymentMethod.replace('_', ' ')}</td>
                    <td className="p-3 text-center">
                      <div className="flex items-center justify-center space-x-2">
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleViewReceipt(expense)}
                          className="p-2 text-blue-600 hover:text-blue-800 hover:bg-blue-50"
                          title="View Receipt"
                        >
                          <Eye className="w-4 h-4" />
                        </Button>
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleEditExpense(expense)}
                          className="p-2 text-green-600 hover:text-green-800 hover:bg-green-50"
                          title="Edit Expense"
                        >
                          <Edit className="w-4 h-4" />
                        </Button>
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleDeleteExpense(expense)}
                          className="p-2 text-red-600 hover:text-red-800 hover:bg-red-50"
                          title="Delete Expense"
                        >
                          <Trash2 className="w-4 h-4" />
                        </Button>
                      </div>
                    </td>
                  </tr>
                )) : (
                  <tr>
                    <td colSpan={6} className="p-8 text-center text-muted-foreground">
                      No expenses recorded yet
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </CardContent>
      </Card>

      {/* Delete Confirmation Dialog */}
      <DeleteConfirmation
        isOpen={deleteConfirmOpen}
        onClose={() => setDeleteConfirmOpen(false)}
        onConfirm={confirmDeleteExpense}
        title="Delete Expense"
        description="Are you sure you want to delete this expense? This action cannot be undone."
        itemName={expenseToDelete?.description || "expense"}
        isLoading={deleteExpenseMutation.isPending}
      />
    </div>
  );
}

