import { useParams } from "wouter";
import { useQuery } from "@tanstack/react-query";
import { useEffect } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { useAuth } from "@/hooks/useAuth";
import { useStation } from "@/contexts/StationContext";
import { formatAmount } from "@/lib/currency";
import { Printer, Download, ArrowLeft, ChevronDown } from "lucide-react";
import { Link } from "wouter";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";
import type { PurchaseOrder, Supplier, Station, User } from "@shared/schema";

interface PurchaseOrderWithDetails extends PurchaseOrder {
  supplier: Supplier;
  user: User;
  station: Station;
}

export default function PurchaseInvoice() {
  const { id } = useParams<{ id: string }>();
  const { user } = useAuth();
  const { stationSettings, isLoading: stationLoading } = useStation();

  const { data: order, isLoading } = useQuery<PurchaseOrderWithDetails>({
    queryKey: ["/api/purchase-orders/detail", id!],
    enabled: !!id && !!user?.stationId,
  });

  const handlePrint = () => {
    window.print();
  };

  const handleDownloadPDF = () => {
    const printContent = document.getElementById('purchase-invoice-print');
    if (!printContent) return;

    const printWindow = window.open('', '_blank');
    if (!printWindow) return;

    const htmlContent = `
      <!DOCTYPE html>
      <html>
        <head>
          <title>Purchase Order ${order?.orderNumber || 'Unknown'}</title>
          <style>
            @page { margin: 0.5in; size: A4; }
            body { font-family: Arial, sans-serif; line-height: 1.4; color: #000; margin: 0; padding: 20px; }
            .container { max-width: 800px; margin: 0 auto; }
            .header { display: flex; justify-content: space-between; align-items: start; margin-bottom: 30px; border-bottom: 2px solid #333; padding-bottom: 20px; }
            .station-info h1 { color: #2563eb; font-size: 28px; margin: 0; }
            .station-info p { margin: 5px 0; color: #666; }
            .order-title { font-size: 24px; font-weight: bold; text-align: right; }
            .order-meta { text-align: right; margin-top: 10px; }
            .order-meta p { margin: 5px 0; }
            .section { margin-bottom: 30px; }
            .section h3 { background: #f3f4f6; padding: 10px; margin: 0 0 15px 0; font-size: 16px; }
            .details-grid { display: grid; grid-template-columns: 1fr 1fr; gap: 20px; }
            .detail-item { margin-bottom: 10px; }
            .detail-label { font-weight: bold; color: #374151; }
            .detail-value { color: #6b7280; }
            .totals { background: #f9fafb; padding: 20px; border-radius: 8px; margin-top: 20px; }
            .total-row { display: flex; justify-content: space-between; margin-bottom: 10px; }
            .total-row.final { border-top: 2px solid #333; padding-top: 10px; margin-top: 15px; font-weight: bold; font-size: 18px; }
            .footer { text-align: center; margin-top: 40px; padding-top: 20px; border-top: 1px solid #e5e7eb; color: #6b7280; font-size: 12px; }
            .status-badge { background: #10b981; color: white; padding: 5px 15px; border-radius: 20px; font-size: 12px; }
          </style>
        </head>
        <body>
          <div class="container">
            <div class="header">
              <div class="station-info">
                <h1>${order?.station?.name || "FuelFlow Station"}</h1>
                <p>Purchase Order Invoice</p>
                <p>Generated on ${new Date().toLocaleDateString()}</p>
              </div>
              <div>
                <div class="order-title">PURCHASE ORDER</div>
                <div class="order-meta">
                  <p><strong>PO #:</strong> ${order?.orderNumber}</p>
                  <p><strong>Date:</strong> ${new Date(order?.orderDate || new Date()).toLocaleDateString()}</p>
                  ${order?.expectedDeliveryDate ? `<p><strong>Expected Delivery:</strong> ${new Date(order.expectedDeliveryDate).toLocaleDateString()}</p>` : ''}
                  <div class="status-badge">${order?.status?.toUpperCase()}</div>
                </div>
              </div>
            </div>

            <div class="section">
              <h3>Supplier Information</h3>
              <div class="details-grid">
                <div>
                  <div class="detail-item">
                    <div class="detail-label">Company Name:</div>
                    <div class="detail-value">${order?.supplier?.name || 'N/A'}</div>
                  </div>
                  ${order?.supplier?.contactPerson ? `
                  <div class="detail-item">
                    <div class="detail-label">Contact Person:</div>
                    <div class="detail-value">${order.supplier.contactPerson}</div>
                  </div>` : ''}
                </div>
                <div>
                  ${order?.supplier?.contactPhone ? `
                  <div class="detail-item">
                    <div class="detail-label">Phone:</div>
                    <div class="detail-value">${order.supplier.contactPhone}</div>
                  </div>` : ''}
                  ${order?.supplier?.contactEmail ? `
                  <div class="detail-item">
                    <div class="detail-label">Email:</div>
                    <div class="detail-value">${order.supplier.contactEmail}</div>
                  </div>` : ''}
                </div>
              </div>
            </div>

            ${order?.notes ? `
            <div class="section">
              <h3>Order Notes</h3>
              <p>${order.notes}</p>
            </div>` : ''}

            <div class="totals">
              <div class="total-row">
                <span>Subtotal:</span>
                <span>${formatAmount(parseFloat(order?.subtotal || '0'), order?.currencyCode || 'PKR')}</span>
              </div>
              ${parseFloat(order?.taxAmount || '0') > 0 ? `
              <div class="total-row">
                <span>Tax:</span>
                <span>${formatAmount(parseFloat(order?.taxAmount || '0'), order?.currencyCode || 'PKR')}</span>
              </div>` : ''}
              <div class="total-row final">
                <span>Total Amount:</span>
                <span>${formatAmount(parseFloat(order?.totalAmount || '0'), order?.currencyCode || 'PKR')}</span>
              </div>
            </div>

            <div class="footer">
              <p>This is a computer-generated purchase order from FuelFlow Management System</p>
              <p>Generated on ${new Date().toLocaleString()}</p>
            </div>
          </div>
        </body>
      </html>
    `;

    printWindow.document.write(htmlContent);
    printWindow.document.close();

    printWindow.onload = () => {
      setTimeout(() => {
        printWindow.print();
        printWindow.close();
      }, 500);
    };
  };

  if (isLoading || stationLoading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="animate-pulse">
          <div className="h-8 bg-muted rounded w-64 mb-4"></div>
          <div className="h-4 bg-muted rounded w-48 mb-8"></div>
          <div className="h-96 bg-muted rounded"></div>
        </div>
      </div>
    );
  }

  if (!order) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <Card className="w-full max-w-md">
          <CardContent className="text-center p-6">
            <h2 className="text-2xl font-bold mb-2">Purchase Order Not Found</h2>
            <p className="text-muted-foreground mb-4">
              The requested purchase order could not be found.
            </p>
            <Link href="/purchase-orders">
              <Button>
                <ArrowLeft className="w-4 h-4 mr-2" />
                Back to Purchase Orders
              </Button>
            </Link>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      {/* Print/Download Actions - Hidden when printing */}
      <div className="print:hidden sticky top-0 bg-background/80 backdrop-blur-sm border-b z-10">
        <div className="container mx-auto px-6 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-4">
              <Link href="/purchase-orders">
                <Button variant="ghost" size="sm">
                  <ArrowLeft className="w-4 h-4 mr-2" />
                  Back
                </Button>
              </Link>
              <h1 className="text-2xl font-bold">Purchase Order #{order.orderNumber}</h1>
            </div>
            <div className="flex items-center gap-2">
              <Button onClick={handlePrint} size="sm">
                <Printer className="w-4 h-4 mr-2" />
                Print
              </Button>
              <Button onClick={handleDownloadPDF} variant="outline" size="sm">
                <Download className="w-4 h-4 mr-2" />
                Download PDF
              </Button>
            </div>
          </div>
        </div>
      </div>

      {/* Invoice Content */}
      <div className="container mx-auto px-6 py-8 max-w-4xl">
        <Card className="print:shadow-none print:border-none">
          <CardContent className="p-8" id="purchase-invoice-print">
            {/* Header */}
            <div className="flex justify-between items-start mb-8">
              <div>
                <h1 className="text-4xl font-bold text-primary mb-2">
                  {stationSettings?.stationName || 'FuelFlow Station'}
                </h1>
                <div className="text-muted-foreground space-y-1">
                  <p>Purchase Order Invoice</p>
                </div>
              </div>
              <div className="text-right">
                <h2 className="text-3xl font-bold mb-2">PURCHASE ORDER</h2>
                <div className="space-y-1 text-sm">
                  <p><span className="font-semibold">PO #:</span> {order.orderNumber}</p>
                  <p><span className="font-semibold">Date:</span> {new Date(order.orderDate || new Date()).toLocaleDateString()}</p>
                  {order.expectedDeliveryDate && (
                    <p><span className="font-semibold">Expected Delivery:</span> {new Date(order.expectedDeliveryDate).toLocaleDateString()}</p>
                  )}
                </div>
              </div>
            </div>

            {/* Status Badge */}
            <div className="flex gap-2 mb-6">
              <Badge variant="outline">
                {order.status?.toUpperCase()}
              </Badge>
            </div>

            {/* Supplier Info */}
            <div className="grid md:grid-cols-2 gap-8 mb-8">
              <div>
                <h3 className="font-semibold text-lg mb-3">Supplier:</h3>
                <div className="space-y-1">
                  <p className="font-semibold">{order.supplier?.name}</p>
                  {order.supplier?.contactPerson && (
                    <p>Contact: {order.supplier.contactPerson}</p>
                  )}
                  {order.supplier?.contactPhone && (
                    <p>Phone: {order.supplier.contactPhone}</p>
                  )}
                  {order.supplier?.contactEmail && (
                    <p>Email: {order.supplier.contactEmail}</p>
                  )}
                </div>
              </div>
              <div>
                <h3 className="font-semibold text-lg mb-3">Order Details:</h3>
                <div className="space-y-1 text-sm">
                  {order.notes && (
                    <p><span className="font-semibold">Notes:</span> {order.notes}</p>
                  )}
                </div>
              </div>
            </div>

            {/* Totals */}
            <div className="flex justify-end mb-8">
              <div className="w-full max-w-sm space-y-2">
                <div className="flex justify-between">
                  <span>Subtotal:</span>
                  <span>{formatAmount(parseFloat(order.subtotal || '0'), order.currencyCode || 'PKR')}</span>
                </div>
                {parseFloat(order.taxAmount || '0') > 0 && (
                  <div className="flex justify-between">
                    <span>Tax:</span>
                    <span>{formatAmount(parseFloat(order.taxAmount || '0'), order.currencyCode || 'PKR')}</span>
                  </div>
                )}
                <div className="flex justify-between text-lg font-semibold border-t pt-2">
                  <span>Total Amount:</span>
                  <span>{formatAmount(parseFloat(order.totalAmount || '0'), order.currencyCode || 'PKR')}</span>
                </div>
              </div>
            </div>

            {/* Footer */}
            <div className="border-t pt-4 text-center text-sm text-muted-foreground">
              <p>Purchase Order generated on {new Date().toLocaleDateString()}</p>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/components/auth/SignupForm.ts.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { Fuel, ArrowLeft } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useLocation } from "wouter";

interface SignupFormProps {
  onBack: () => void;
}

export default function SignupForm({ onBack }: SignupFormProps) {
  const [formData, setFormData] = useState({
    username: "",
    password: "",
    confirmPassword: "",
    fullName: "",
    role: "cashier" as const,
  });
  const [isLoading, setIsLoading] = useState(false);
  const [showApprovalMessage, setShowApprovalMessage] = useState(false);
  const { toast } = useToast();
  const [, navigate] = useLocation();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (formData.password !== formData.confirmPassword) {
      toast({
        title: "Password mismatch",
        description: "Passwords do not match",
        variant: "destructive",
      });
      return;
    }

    setIsLoading(true);

    try {
      const response = await apiRequest("POST", "/api/auth/signup", {
        username: formData.username,
        password: formData.password,
        fullName: formData.fullName,
        role: formData.role,
        isActive: false, // New users start inactive and need approval
      });

      if (response.ok) {
        const result = await response.json();

        // Check if it's an admin account (automatically active)
        if (formData.role === 'admin') {
          toast({
            title: "Admin account created",
            description: "Your admin account has been created successfully. You can now login.",
          });
          onBack(); // Go back to login form
        } else {
          setShowApprovalMessage(true);
          toast({
            title: "Account created",
            description: "Your account has been created successfully. Please wait for admin approval.",
          });
          // Instead of navigating to login, redirect to approval pending page
          navigate("/approval-pending");
        } 
      } else {
        const error = await response.json();
        throw new Error(error.message || "Failed to create account");
      }
    } catch (error: any) {
      toast({
        title: "Signup failed",
        description: error.message || "Failed to create account",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  if (showApprovalMessage) {
    return (
      <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 p-4">
        <Card className="w-full max-w-sm border shadow-xl">
          <CardContent className="pt-6">
            <div className="text-center mb-6">
              <div className="w-16 h-16 bg-orange-500 rounded-full flex items-center justify-center mx-auto mb-4">
                <Fuel className="w-8 h-8 text-white" />
              </div>
              <h2 className="text-2xl font-bold text-card-foreground">Account Created!</h2>
              <p className="text-muted-foreground">Awaiting Admin Approval</p>
            </div>

            <div className="text-center space-y-4">
              <div className="p-4 bg-orange-50 dark:bg-orange-900/20 rounded-lg border border-orange-200 dark:border-orange-800">
                <p className="text-sm text-orange-800 dark:text-orange-200">
                  Your account has been created successfully. Please wait for an administrator to approve your account before you can log in.
                </p>
              </div>

              <div className="flex items-center justify-center space-x-2 text-sm text-muted-foreground">
                <div className="w-2 h-2 bg-orange-500 rounded-full animate-pulse"></div>
                <span>Waiting for approval...</span>
              </div>
            </div>

            <div className="mt-6">
              <Button
                type="button"
                variant="outline"
                className="w-full"
                onClick={onBack}
              >
                <ArrowLeft className="mr-2 h-4 w-4" />
                Back to Login
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 p-4">
      <Card className="w-full max-w-sm border shadow-xl">
        <CardContent className="pt-6">
          <div className="text-center mb-6">
            <div className="w-16 h-16 bg-primary rounded-full flex items-center justify-center mx-auto mb-4">
              <Fuel className="w-8 h-8 text-primary-foreground" />
            </div>
            <h2 className="text-2xl font-bold text-card-foreground">Create Account</h2>
            <p className="text-muted-foreground">Join FuelFlow System</p>
          </div>

          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <Label htmlFor="fullName" className="block text-sm font-medium text-card-foreground mb-2">
                Full Name
              </Label>
              <Input
                id="fullName"
                type="text"
                value={formData.fullName}
                onChange={(e) => setFormData(prev => ({ ...prev, fullName: e.target.value }))}
                placeholder="Enter your full name"
                className="w-full"
                required
              />
            </div>

            <div>
              <Label htmlFor="username" className="block text-sm font-medium text-card-foreground mb-2">
                Username
              </Label>
              <Input
                id="username"
                type="text"
                value={formData.username}
                onChange={(e) => setFormData(prev => ({ ...prev, username: e.target.value }))}
                placeholder="Choose a username"
                className="w-full"
                required
              />
            </div>

            <div>
              <Label htmlFor="role" className="block text-sm font-medium text-card-foreground mb-2">
                Role
              </Label>
              <Select value={formData.role} onValueChange={(value: "cashier" | "manager") => setFormData(prev => ({ ...prev, role: value }))}>
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="cashier">Cashier</SelectItem>
                  <SelectItem value="manager">Manager</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div>
              <Label htmlFor="password" className="block text-sm font-medium text-card-foreground mb-2">
                Password
              </Label>
              <Input
                id="password"
                type="password"
                value={formData.password}
                onChange={(e) => setFormData(prev => ({ ...prev, password: e.target.value }))}
                placeholder="Create a password"
                className="w-full"
                required
              />
            </div>

            <div>
              <Label htmlFor="confirmPassword" className="block text-sm font-medium text-card-foreground mb-2">
                Confirm Password
              </Label>
              <Input
                id="confirmPassword"
                type="password"
                value={formData.confirmPassword}
                onChange={(e) => setFormData(prev => ({ ...prev, confirmPassword: e.target.value }))}
                placeholder="Confirm your password"
                className="w-full"
                required
              />
            </div>

            <Button 
              type="submit" 
              className="w-full" 
              disabled={isLoading}
            >
              {isLoading ? "Creating Account..." : "Create Account"}
            </Button>
          </form>

          <div className="mt-4">
            <Button
              type="button"
              variant="outline"
              className="w-full"
              onClick={onBack}
            >
              <ArrowLeft className="mr-2 h-4 w-4" />
              Back to Login
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/ui/delete-confirma.content:
import React from "react";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";

interface DeleteConfirmationProps {
  isOpen: boolean;
  onClose: () => void;
  onConfirm: () => void;
  title?: string;
  description?: string;
  itemName?: string;
  isLoading?: boolean;
}

export function DeleteConfirmation({
  isOpen,
  onClose,
  onConfirm,
  title = "Are you absolutely sure?",
  description,
  itemName = "this item",
  isLoading = false,
}: DeleteConfirmationProps) {
  const defaultDescription = `This action cannot be undone. This will permanently delete ${itemName} and remove all associated data from our servers.`;

  return (
    <AlertDialog open={isOpen} onOpenChange={onClose}>
      <AlertDialogContent>
        <AlertDialogHeader>
          <AlertDialogTitle>{title}</AlertDialogTitle>
          <AlertDialogDescription>
            {description || defaultDescription}
          </AlertDialogDescription>
        </AlertDialogHeader>
        <AlertDialogFooter>
          <AlertDialogCancel onClick={onClose} disabled={isLoading}>
            Cancel
          </AlertDialogCancel>
          <AlertDialogAction
            onClick={onConfirm}
            disabled={isLoading}
            className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
          >
            {isLoading ? "Deleting..." : "Delete"}
          </AlertDialogAction>
        </AlertDialogFooter>
      </AlertDialogContent>
    </AlertDialog>
  );
}

client/src/pages/ApprovalPending.tsx.content:
import { useEffect, useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { CheckCircle, Clock, User, AlertCircle } from "lucide-react";
import { useAuth } from "@/hooks/useAuth";
import { useLocation } from "wouter";
import { useToast } from "@/hooks/use-toast";

interface ApprovalPendingProps {
  userEmail?: string;
  userName?: string;
}

export default function ApprovalPending({ userEmail, userName }: ApprovalPendingProps) {
  const { user, logout } = useAuth();
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [isApproved, setIsApproved] = useState(false);

  // Check approval status periodically
  useEffect(() => {
    const checkApprovalStatus = async () => {
      if (!userEmail && !user?.username) return;

      try {
        // Try to login with stored credentials to check if approved
        const username = userEmail || user?.username;
        if (username) {
          // This is just a status check - in real implementation, you'd have a separate endpoint
          // For now, we'll just show the pending state
        }
      } catch (error) {
        // Still pending
      }
    };

    const interval = setInterval(checkApprovalStatus, 30000); // Check every 30 seconds
    return () => clearInterval(interval);
  }, [userEmail, user]);

  const handleBackToLogin = () => {
    logout();
    setLocation("/login");
  };

  const handleCheckStatus = () => {
    toast({
      title: "Status Checked",
      description: "Your account is still pending approval. Please wait for administrator action.",
      variant: "default",
    });
  };

  if (isApproved) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-green-50 to-emerald-100 flex items-center justify-center p-4">
        <Card className="w-full max-w-md">
          <CardHeader className="text-center pb-6">
            <div className="mx-auto w-16 h-16 bg-green-100 rounded-full flex items-center justify-center mb-4">
              <CheckCircle className="w-8 h-8 text-green-600" />
            </div>
            <CardTitle className="text-2xl text-gray-900">Account Approved!</CardTitle>
          </CardHeader>
          <CardContent className="text-center space-y-4">
            <div className="space-y-2">
              <p className="text-gray-600">
                Great news! Your account has been approved.
              </p>
              <p className="text-gray-600">
                You can now log in to access the FuelFlow system.
              </p>
            </div>

            <div className="pt-4">
              <Button 
                onClick={handleBackToLogin}
                className="w-full"
              >
                Continue to Login
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-4">
      <Card className="w-full max-w-md">
        <CardHeader className="text-center pb-6">
          <div className="mx-auto w-16 h-16 bg-orange-100 rounded-full flex items-center justify-center mb-4">
            <Clock className="w-8 h-8 text-orange-600" />
          </div>
          <CardTitle className="text-2xl text-gray-900">Account Pending Approval</CardTitle>
        </CardHeader>
        <CardContent className="text-center space-y-4">
          <div className="space-y-2">
            <p className="text-gray-600">
              Thank you for registering{userName ? `, ${userName}` : ''}!
            </p>
            <p className="text-gray-600">
              Your account is currently pending approval from an administrator.
            </p>
          </div>

          <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
            <div className="flex items-center text-blue-800 mb-2">
              <User className="w-5 h-5 mr-2" />
              <span className="font-medium">What's next?</span>
            </div>
            <p className="text-sm text-blue-700">
              An administrator will review your account and activate it. You'll be able to log in once approved.
            </p>
          </div>

          <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4">
            <div className="flex items-center text-yellow-800 mb-2">
              <AlertCircle className="w-5 h-5 mr-2" />
              <span className="font-medium">Need help?</span>
            </div>
            <p className="text-sm text-yellow-700">
              Contact your system administrator if you believe this is an error or if you've been waiting for an extended period.
            </p>
          </div>

          <div className="pt-4 space-y-2">
            <Button 
              onClick={handleCheckStatus}
              variant="outline" 
              className="w-full"
            >
              Check Status
            </Button>
            <Button 
              onClick={handleBackToLogin}
              variant="ghost" 
              className="w-full"
            >
              Back to Login
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

